mapper.insert:

insert into RAC_ORG (ID, NAME, PARENT_ID, 
  ORG_TYPE, LEFT_VALUE, RIGHT_VALUE, 
  FULL_NAME, INTRODUCTION, REMARK
  )
values (#{id,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{parentId,jdbcType=BIGINT}, 
  #{orgType,jdbcType=TINYINT}, #{leftValue,jdbcType=INTEGER}, #{rightValue,jdbcType=INTEGER}, 
  #{fullName,jdbcType=VARCHAR}, #{introduction,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )

mapper.select:

select 

from RAC_ORG


    and NAME = #{name,jdbcType=VARCHAR}


    and PARENT_ID = #{parentId,jdbcType=BIGINT}


    and ORG_TYPE = #{orgType,jdbcType=TINYINT}


    and LEFT_VALUE = #{leftValue,jdbcType=INTEGER}


    and RIGHT_VALUE = #{rightValue,jdbcType=INTEGER}


    and FULL_NAME = #{fullName,jdbcType=VARCHAR}


    and INTRODUCTION = #{introduction,jdbcType=VARCHAR}


    and REMARK = #{remark,jdbcType=VARCHAR}


select count(*) 
from RAC_ORG
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from RAC_ORG


    and ID = #{id,jdbcType=BIGINT}


    and NAME = #{name,jdbcType=VARCHAR}


    and PARENT_ID = #{parentId,jdbcType=BIGINT}


    and ORG_TYPE = #{orgType,jdbcType=TINYINT}


    and LEFT_VALUE = #{leftValue,jdbcType=INTEGER}


    and RIGHT_VALUE = #{rightValue,jdbcType=INTEGER}


    and FULL_NAME = #{fullName,jdbcType=VARCHAR}


    and INTRODUCTION = #{introduction,jdbcType=VARCHAR}


    and REMARK = #{remark,jdbcType=VARCHAR}


select count(1) 
from RAC_ORG


    and NAME = #{name,jdbcType=VARCHAR}


    and PARENT_ID = #{parentId,jdbcType=BIGINT}


    and ORG_TYPE = #{orgType,jdbcType=TINYINT}


