mapper.select:

select 

from RAC_USER


    and NICKNAME = #{nickname,jdbcType=VARCHAR}


    and AVATAR = #{avatar,jdbcType=VARCHAR}


    and LOGIN_NAME = #{loginName,jdbcType=VARCHAR}


    and LOGIN_PSWD = #{loginPswd,jdbcType=VARCHAR}


    and PAY_PSWD = #{payPswd,jdbcType=VARCHAR}


    and SALT = #{salt,jdbcType=CHAR}


    and MOBILE = #{mobile,jdbcType=VARCHAR}


    and IS_VERIFIED_MOBILE = #{isVerifiedMobile,jdbcType=BIT}


    and EMAIL = #{email,jdbcType=VARCHAR}


    and IS_VERIFIED_EMAIL = #{isVerifiedEmail,jdbcType=BIT}


    and WX_OPEN_ID = #{wxOpenId,jdbcType=VARCHAR}


    and WX_UNION_ID = #{wxUnionId,jdbcType=VARCHAR}


    and WX_NICKNAME = #{wxNickname,jdbcType=VARCHAR}


    and WX_AVATAR = #{wxAvatar,jdbcType=VARCHAR}


    and QQ_OPEN_ID = #{qqOpenId,jdbcType=VARCHAR}


    and QQ_UNION_ID = #{qqUnionId,jdbcType=VARCHAR}


    and QQ_NICKNAME = #{qqNickname,jdbcType=VARCHAR}


    and QQ_AVATAR = #{qqAvatar,jdbcType=VARCHAR}


    and REAL_NAME = #{realName,jdbcType=VARCHAR}


    and IS_VERIFIED_REALNAME = #{isVerifiedRealname,jdbcType=BIT}


    and ID_CARD = #{idCard,jdbcType=CHAR}


    and IS_VERIFIED_IDCARD = #{isVerifiedIdcard,jdbcType=BIT}


    and SEX = #{sex,jdbcType=TINYINT}


    and AGE = #{age,jdbcType=TINYINT}


    and IS_TESTER = #{isTester,jdbcType=BIT}


    and IS_ENABLED = #{isEnabled,jdbcType=BIT}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


select count(*) 
from RAC_USER
where ID = #{id,jdbcType=BIGINT}


